function isJson(n){try{JSON.parse(n)}catch(t){return!1}return!0}function appendModal(n){$("body").append("<div id='"+n+"' class='modal fade in' role='dialog'><\/div>")}function showModal(n,t){appendModal(n);$("#"+n).html(t).modal("show")}function modal(n,t){isJson(n)||(n="{ 'Mensagem':'"+n+"'}");$.get("/Home/Modal",{jsonModal:n,tipo:t},function(n){showModal(n.Id,n)})}function exclui(n,t){var i="/"+n+"/Excluir";$.post(i,{id:t},function(){$("#"+n+t).length?$("#"+n+t).remove():window.location.href="/"+n+"/Index"})}$(document).ready(function(){$(".input-validation-error").parents(".form-group").addClass("has-error")});$(document).on("hidden.bs.modal",".modal",function(){$(this).remove()});$("a[data-toggle=modal]").on("click",function(){var n=$(this).data("target");appendModal(n);$("#"+n).load(this.href).modal("show")});