function getPost(n,t,i){$.getJSON("/"+n+"/Get",{id:i},function(i){alert(JSON.stringify(i));Model=AddAntiForgeryToken(i);$.post("/"+n+"/"+t,Model).done(sucesso).fail(erro)})}function altera(n,t){var i=parseInt($(n).val().replace(/\./g,""));$(n).val(i+t<0?0:(i+t).toLocaleString())}function aumentaAtual(n){n?getPost("Produto","AumentaAtual",n):altera("#EstoqueAtual",1)}function diminuiAtual(n){n?getPost("Produto","DiminuiAtual",n):altera("#EstoqueAtual",-1)}function aumentaMinimo(n){n?getPost("Produto","AumentaMinimo",n):altera("#EstoqueMinimo",1)}function diminuiMinimo(n){n?getPost("Produto","DiminuiMinimo",n):altera("#EstoqueMinimo",-1)}function sucesso(n){$("#EstoqueAtual"+n.Id).html(n.EstoqueAtual.toLocaleString());$("#EstoqueMinimo"+n.Id).html(n.EstoqueMinimo.toLocaleString());$("#PrecisaReposicao"+n.Id).html(n.PrecisaReposicao===!0?"Sim":"N\u00e3o");var t=new Date(parseInt(n.DataAlteracao.substr(6))),i=(t.getDate()<10?"0":"")+t.getDate(),r=(t.getMonth()<9?"0":"")+(t.getMonth()+1),u=(t.getFullYear()<10?"0":"")+t.getFullYear(),f=(t.getHours()<10?"0":"")+t.getHours(),e=(t.getMinutes()<10?"0":"")+t.getMinutes(),o=(t.getSeconds()<10?"0":"")+t.getSeconds();$("#DataAlteracao"+n.Id).html(i+"/"+r+"/"+u+" @ "+f+":"+e+":"+o)}function erro(n){modal(n.statusText,"Erro")}$(document).ready(function(){$("#Preco").on("paste focusout",function(){var n=$(this).val().toLocaleString();alert(n)});$("#EstoqueAtual, #EstoqueMinimo").on("paste focusout",function(){})});